import React, { useContext, useEffect, useState } from "react";
import { UidContext } from "../components/AppContext";
import LeftNav from "../components/LeftNav";
import { getEvents } from "../actions/events";
import { useSelector } from "react-redux";
import Log from "../components/Log";
import { useDispatch } from "react-redux";
import axios from "axios";
import "./style.css";
const Home = () => {
  const uid = useContext(UidContext);

  const [events, setEvents] = useState([]);
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [picture, setPicture] = useState("");
  const [category, setCategory] = useState("");
  useEffect(() => {
    const fetchEvents = async () => {
      await axios
        .get(`${process.env.REACT_APP_API_URL}api/events/getevents/`)
        .then((res) => {
          setEvents(res.data);
        })
        .catch((err) => console.log("No Events"));
    };
    fetchEvents();
  }, []);

  const filterEvent = (event) => {
    console.log(event);
  };
  const handleSubmit = (e) => {
    e.preventDefault();
    const data = new FormData();
    data.append("title", title);
    data.append("description",description);
    data.append("picture", picture);
    data.append("category", category);

    axios
      .post(`${process.env.REACT_APP_API_URL}api/events/addevents/`,data,
      )
      .then((res) => {
       alert(res);
      })
      .catch((err) => alert("error",data));
  };
  return (
    <>
      <div className="home">
        <div className="main">
          <div className="home-header">
            {uid ? (
              <div>
                <form onSubmit={handleSubmit}>
                  <div Name="mb-3">
                    <label for="tite" className="form-label">
                      tite de l'evenement
                    </label>
                    <input
                      value={title}
                      onChange={(e) => {
                        setTitle(e.target.value);
                      }}
                      type="text"
                      className="form-control"
                      name="title"
                      id="title"
                      placeholder="title"
                    />
                    <label for="descrption" className="form-label">
                      description
                    </label>
                    <textarea
                      value={description}
                      onChange={(e) => {
                        setDescription(e.target.value);
                      }}
                      type="text"
                      className="form-control"
                      id="description"
                      name="description"
                      placeholder="...."
                    />
                    <label for="category" className="form-label">
                      Catégorie
                    </label>
                    <input
                      value={category}
                      onChange={(e) => {
                        setCategory(e.target.value);
                      }}
                      type="text"
                      className="form-control"
                      name="category"
                      id="category"
                      placeholder="category"
                    />

                    <label for="picture" className="form-label">
                      image
                    </label>
                    <input
                      value={picture}
                      onChange={(e) => {
                        setPicture(e.target.value);
                      }}
                      type="file"
                      className="form-control"
                      name="picture"
                      id="picture"
                      placeholder="image"
                    />
                  </div>
                  <button type="submit" className="btn btn-primary">
                    ajouter
                  </button>
                </form>
              </div>
            ) : (
              <>
                <Log signin={true} signup={false} />
                <br />
                <div className="container-fluid events-style">
                  <div className="row">
                    <h3 className="col-lg-9">evenements</h3>
                    <button
                      className="col-lg-1 btn"
                      value="sport"
                      onClick={(e) => {
                        filterEvent(e.target.value);
                      }}
                    >
                      sport
                    </button>
                    <button
                      className="col-lg-1 btn"
                      value="culturel"
                      onClick={(e) => {
                        filterEvent(e.target.value);
                      }}
                    >
                      culturel
                    </button>
                  </div>
                </div>

                <div className="section">
                  <div className="row">
                    {events ? (
                      events.map((item) => {
                        return (
                          <div className="col-lg-3 col">
                            <h3 className="titre">{item.title}</h3>
                            <p>
                              <br></br>•{item.description}
                            </p>
                          </div>
                        );
                      })
                    ) : (
                      <div></div>
                    )}
                  </div>
                </div>
              </>
            )}
          </div>
        </div>
      </div>
    </>
  );
};

export default Home;
