import React, { useContext, useEffect,useState } from "react";
import { UidContext } from "../components/AppContext";
import LeftNav from "../components/LeftNav";
import { getEvents } from "../actions/events";
import { useSelector } from "react-redux";
import Log from "../components/Log";
import { useDispatch } from "react-redux";
import axios from "axios";
import './style.css'
const Home = () => {
  const uid = useContext(UidContext);

const [events,setEvents]=useState([])
useEffect(() => {
  const fetchEvents = async () => {
    await axios
    .get(`${process.env.REACT_APP_API_URL}api/events/getevents/`)
      .then((res) => {
        setEvents(res.data);
      })
      .catch((err) => console.log("No Events"));
  };
  fetchEvents();
}, [events]);

const filterEvent = (event)=>{
console.log(event)
}

  return (
    <>
      <div className="home">
      
        <div className="main">
          <div className="home-header">
            {uid ? (
              <div>
              <form>
  <div class="mb-3">
    <label for="exampleInputEmail1" class="form-label">Email address</label>
    <input type="email" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp"/>
    <div id="emailHelp" class="form-text">We'll never share your email with anyone else.</div>
  </div>
  <div class="mb-3">
    <label for="exampleInputPassword1" class="form-label">Password</label>
    <input type="password" class="form-control" id="exampleInputPassword1"/>
  </div>
  <div class="mb-3 form-check">
    <input type="checkbox" class="form-check-input" id="exampleCheck1"/>
    <label class="form-check-label" for="exampleCheck1">Check me out</label>
  </div>
  <button type="submit" class="btn btn-primary">Submit</button>
</form>
              </div>
            ) : (
              <>
                <Log signin={true} signup={false} />
                <br/>
                <div className="container-fluid events-style">
                <div className="row">
                <h3 className="col-lg-9">evenements</h3>
                <button className="col-lg-1 btn" value='sport' onClick={(e)=>{filterEvent(e.target.value)}}>sport</button>
                <button className="col-lg-1 btn"  value='culturel' onClick={(e)=>{filterEvent(e.target.value)}}>culturel</button>
                </div>
                </div>
                
                <div className="section">
                <div className="row"> 
                  {
                    events ? events.map((item) => {
                    return (
                     
                      <div className="col-lg-3 col">
                        <h3 className="titre">{item.title}</h3>
                        <p>
                          <br></br>â€¢{item.description}
                         
                        </p>
                      </div>
                      
                    );
                  }):<div></div>
                }
                </div>
                </div>
              </>
            )}
          </div>
        </div>
      </div>
    </>
  );
};

export default Home;
